import QuoteList from "../components/quotes/QuoteList";
import useHttp from "../hooks/use-http";
import { getAllQuotes } from "../lib/api";
import { useEffect } from "react";
import LoadingSpinner from "../components/ui/LoadingSpinner";
import NotFound from "./NotFound";
const DUMMY_QUOTES = [
  { id: "q1", author: "Max", text: "Learning is fun" },
  { id: "q2", author: "Manu", text: "Learning is fun too" },
  {
    id: "q3",
    author: "Leo",
    text: "Learning is fun if you do not know what you are learning",
  },
  {
    id: "q4",
    author: "EZ",
    text: "Learning is fun but it is not fun if you do not know what you are learning",
  },
];
const AllQuotes = () => {
  const {
    sendRequest,
    status,
    data: loadedQuotes,
    error
  } = useHttp(getAllQuotes, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);
  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if(error){
    return <div className="centered focused">
      <p>An error occurred. Please try again later.</p>
    </div>
  }
  if(status === 'sucess' && 
  !loadedQuotes || loadedQuotes.length === 0){
    return <NotFound />
  }

  return <QuoteList quotes={DUMMY_QUOTES} />;
};
export default AllQuotes;
