import { Fragment } from "react";
import { useParams, Route } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
  { id: "q1", author: "Max", text: "Learning is fun" },
  { id: "q2", author: "Manu", text: "Learning is fun too" },
  {
    id: "q3",
    author: "Leo",
    text: "Learning is fun if you do not know what you are learning",
  },
  {
    id: "q4",
    author: "EZ",
    text: "Learning is fun but it is not fun if you do not know what you are learning",
  },
];

const Quotedetail = () => {
  const quoteId = useParams().quoteId;
  const quote = DUMMY_QUOTES.find((q) => q.id === quoteId);
  if(!quote) {
    return <p>no quote found!</p>
 }
  return (
    <Fragment>
      <h1>Quote Detail Page</h1>
      <Route path={`/quotes/${quoteId}/comments`}>
        <Comments  />
      </Route>
      <HighlightedQuote text={quote.text} author={quote.author} />
    </Fragment>
  );
};
export default Quotedetail;
